   "source": [
    "# step 6 - build svmLinear classifier with different values of C\n",
    "grid <- expand.grid(\n",
    "    C = c(.01, .05, .1, .25, .5, .75, 1, 1.25, 1.5, 1.75, 2, 5)\n",
    ")\n",
    "svm_Linear_Grid <- train(\n",
    "    target ~ .,\n",
    "    data = training, \n",
    "    method = \"svmLinear\",\n",
    "    trControl = trainCtrl,\n",
    "    preProcess = c(\"center\", \"scale\"),\n",
    "    tuneGrid = grid,\n",
    "    tuneLength = 10\n",
    ")\n",
    "\n",
    "svm_Linear_Grid\n",
    "plot(svm_Linear_Grid) \n",
    "\n",
    "# our classifier is giving best accuracy on C = 0.1. Note: C will change each running.\n",
    "# Let's try to make predictions using this model for our test set\n",
    "test_pred_grid <- predict(\n",
    "    svm_Linear_Grid, \n",
    "    newdata = testing\n",
    ")\n",
    "\n",
    "test_pred_grid\n",
    "\n",
    "# Let's check its accuracy using confusion matrix\n",
    "confusionMatrix(\n",
    "    table(test_pred_grid, testing$target)\n",
    ")\n",
    "# the confusion matrix show the accuracy on the test set is 86.32%\n",
    "# which is more accurate than our previous result (85.34%)."
   ]
  }
